@section('scripts')
    <script>
        function toggleCard(header) {
            const card = header.parentElement;
            const offreCard = document.getElementById("trajet-cardOffre");
            const mTrajetsCard = document.getElementById("trajet-cardMesTrajets");

            const currentHeight = parseInt(window.getComputedStyle(card).height, 10);
            const offreCardHeight = parseInt(window.getComputedStyle(offreCard).height, 10);
            const mTrajetsHeight = parseInt(window.getComputedStyle(mTrajetsCard).height, 10);
            
            if (currentHeight < 700){
                card.style.height = "773px";
                card.querySelector('.trajet-cardContent').style.display = "block";
                card.style.zIndex = "2";
                card.querySelector('.svg').style.transform = "rotate(0deg)"
                if(card === mTrajetsCard && currentHeight < 700){
                    offreCard.style.zIndex = "3";
                }
                if(card === mTrajetsCard){
                    offreCard.style.height = "164px";
                    offreCard.querySelector('.trajet-cardContent').style.display = "none";
                    offreCard.querySelector('.svg').style.transform = "rotate(180deg)"
                }
                if(card === offreCard){
                    mTrajetsCard.style.height = "164px";
                    mTrajetsCard.querySelector('.trajet-cardContent').style.display = "none";
                    mTrajetsCard.querySelector('.svg').style.transform = "rotate(180deg)"
                }
            } else {
                if (mTrajetsHeight === 164 || offreCardHeight === 164){
                    card.querySelector('.svg').style.transform = "rotate(180deg)"
                    card.style.height = "271px";
                    card.style.zIndex = "2";
                } else if(mTrajetsHeight === 271 || offreCardHeight === 271){
                    card.querySelector('.svg').style.transform = "rotate(180deg)"
                    card.style.height = "164px";
                    card.style.zIndex = "3";
                }

                card.querySelector(".trajet-cardContent").style.display = "none";
                card.querySelector('.svg').style.transform = "rotate(180deg)"

                if (mTrajetsHeight < 700 && offreCardHeight < 700) {
                    offreCard.style.height = "164px";
                    offreCard.style.zIndex = "3";
                    offreCard.querySelector('.svg').style.transform = "rotate(180deg)"
                    mTrajetsCard.querySelector('.svg').style.transform = "rotate(180deg)"
                    mTrajetsCard.style.height = "271px";
                    mTrajetsCard.style.zIndex = "2";
                    mTrajetsCard.querySelector('.trajet-cardContent').style.display = "none";
                    offreCard.querySelector('.trajet-cardContent').style.display = "none";
                }
            }
        }



        const filtreDropdown = document.getElementById("filtreDropdown");
        const filtreOpen = document.getElementById('filtreOffre-btnDropdown');
        const filtreValidBtn = document.getElementById('filtreDropdown-validBtn');
        const filtreCancelBtn = document.getElementById('filtreDropdown-cancelBtn');

        if (filtreOpen && filtreDropdown) {

            filtreOpen.addEventListener("click", (event) => {
                event.stopPropagation();
                filtreDropdown.classList.remove("hidden");
                filtreDropdown.classList.add("flex");
            });

            filtreValidBtn.addEventListener('click', (event) => {
                event.stopPropagation();
                filtreDropdown.classList.remove('flex');
                filtreDropdown.classList.add('hidden');
            });

            filtreCancelBtn.addEventListener('click', (event) => {
                event.stopPropagation();
                filtreDropdown.classList.remove('flex');
                filtreDropdown.classList.add('hidden');
            });
        }
    </script>
@endsection